@include 
<template>
    <section class="header">

      <div class="menu">
        <div><span>Homme</span> <span>Femme</span> Concept</div>
        <div><img class="logo" src="/img/logo.3c0e489e.svg" alt="logo"></div>
        <div><img  class="cart" src="../assets/images/icon-cart.png" alt="Cart"></div>
      </div>
      <div class="mainTitle">
        <h1>Femme</h1>
        <span>DÃ©couvrir</span>
      </div>
    </section>
</template>

<script>
export default {
    name: 'HeaderTop',
    mounted() {
      window.addEventListener('scroll', () => {
        let positionScroll = window.scrollY
        let menu = document.querySelector('.menu')
        let header = document.querySelector('.header')
        let logo = document.querySelector('.logo')
        let cart = document.querySelector('.cart')
        console.log(logo)

        if (positionScroll > 10) {
          console.log('ok')
          menu.classList.add('active')
          header.classList.add('active')
          logo.src="/img/logo-black.36466466.svg"
          cart.src="/img/icon-black.84a88f1f.png"

        } else {
          menu.classList.remove('active')
          header.classList.remove('active')
          logo.src="/img/logo.3c0e489e.svg"
          cart.src="/img/icon-cart.60837dfc.png"

        }
      })
    }
}
</script>

<style lang="scss">
.header {
  padding: 22px 40px 0px 40px;
  height: 100vh;
  background-image: url('../assets/images/femme.jpeg');
  width: auto;  background-position: 0 0;
  background-size: cover;
  position: relative;
  .menu {
    display: flex;
    justify-content: space-between;
    text-transform: uppercase;
    color: #ffffff;
    font-size: 20px;
    font-weight: bold;
    span {
      padding-right: 60px;
    }

    &.active {
      position: fixed;
      background-color: #ffffff;
      color: #000000;
      z-index: 100;
      width: 100%;
      padding: 20px;
      .st0{fill:#000000;}
    }
  }

  .logo {
    width: auto;
    height: 70px;
    color: #ffffff;
    margin-left: -120px;
  }
  .cart {
    width: auto;
    height: 40px;
  }

  .mainTitle {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: #ffffff;
    text-align: center;
    h1 {
      font-size: 150px;
      text-transform: uppercase;
      padding-bottom: 120px;
    }
    span {
      font-size: 26px;
      border: 2px solid #ffffff;
      padding: 40px 106px;
      text-transform: uppercase;
    }
  }
  &.active {
    padding: 0;
  }
}
</style>
