@include 
<template>
    <section class="header">

      <div class="menu"><span>Homme</span> <span>Femme</span> Concept</div>
      <div><img class="logo" src="../assets/images/logo.svg" alt=""></div>
      <div><img  class="cart" src="../assets/images/icon-cart.png" alt="Cart"></div>
      <div class="mainTitle">
        <h1>Femme</h1>
        <span>DÃ©couvrir</span>
      </div>
    </section>
</template>

<script>
export default {
    name: 'HeaderTop',
    mounted() {
      window.addEventListener('scroll', () => {
        let positionScroll = window.scrollY
        let menu = document.querySelector('.header')
        if (positionScroll > 10) {
          console.log('ok')
          menu.classList.add('active')
        } else {
          menu.classList.remove('active')
        }
      })
    }
}
</script>

<style lang="scss">
.header {
  display: flex;
  justify-content: space-between;
  padding: 22px 40px 0px 40px;
  height: 100vh;
  background-image: url('../assets/images/femme.jpeg');
  width: auto;  background-position: 0 0;
  background-size: cover;
  position: relative;
  .menu {
    text-transform: uppercase;
    color: #ffffff;
    font-size: 20px;
    font-weight: bold;
    span {
      padding-right: 60px;
    }
  }

  .logo {
    width: auto;
    height: 70px;
    color: #ffffff;
    margin-left: -120px;
  }
  .cart {
    width: auto;
    height: 40px;
  }

  .mainTitle {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: #ffffff;
    text-align: center;
    h1 {
      font-size: 150px;
      text-transform: uppercase;
      padding-bottom: 120px;
    }
    span {
      font-size: 26px;
      border: 2px solid #ffffff;
      padding: 40px 106px;
      text-transform: uppercase;
    }
  }

  .active {
    position: fixed;
    background-color: #ffffff;
    color: #000000;
  }
}
</style>
